package com.github.soonboylena.activiti.vModel.uiComponent;import com.github.soonboylena.activiti.vModel.contant.ButtonType;import com.github.soonboylena.activiti.vModel.uiAction.ClientAction;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;/** * @Author: ZhaoPeng * @Date: 2017/11/30 15:33 * @Description: 点击后弹出layer的button */@Data@NoArgsConstructor@AllArgsConstructorpublic class Button4Layer extends Button {    private final String buttonType = "layer-button";    private ClientAction getDataAction;    private ClientAction saveDataAction;    public Button4Layer(String text, ButtonType type, ClientAction getDataAction, ClientAction saveDataAction) {        super.setText(text);        super.setType(type);        this.getDataAction = getDataAction;        this.saveDataAction = saveDataAction;    }}