package com.github.soonboylena.activiti.vModel.uiComponent;import com.github.soonboylena.activiti.vModel.contant.ButtonLocation;import com.github.soonboylena.activiti.vModel.contant.ButtonType;import com.github.soonboylena.activiti.vModel.uiAction.ClientAction;import lombok.AllArgsConstructor;import lombok.Data;import java.util.ArrayList;import java.util.List;/** * @Author: ZhaoPeng * @Date: 2017/11/30 15:33 * @Description: 内部含有button的button */@Data@AllArgsConstructorpublic class ButtonSelect extends Button {    private final String buttonType = "select-button";    private List<Button> child;    public void addButton(Button button) {        if (null == child) {            child = new ArrayList<>();        }        child.add(button);    }    public ButtonSelect(String text, ButtonType type, ClientAction action, ButtonLocation location) {        super(text, type, action, location);    }}