package com.github.soonboylena.activiti.vModel.uiComponent;import com.github.soonboylena.activiti.vModel.AbstractDwc;import com.github.soonboylena.activiti.vModel.IUiDefinition;import lombok.Data;/** * Created with IntelliJ IDEA. * Description: * Project Name:    spring-boot-admin * UserEntity:            sunb * Date:            2017-10-24 * Time:            14:43 */public class Section extends AbstractDwc<SectionUiDefine> {    private transient static final String TYPE = "m-section";    public Section() {        setDefine(new SectionUiDefine());    }    public Section(String title) {        this();        getDefine().setTitle(title);    }    @Override    public String getType() {        return TYPE;    }}@Dataclass SectionUiDefine implements IUiDefinition {    private String title;}