package com.github.soonboylena.myflow.vModel.uiComponent;import com.github.soonboylena.myflow.vModel.IUiDefinition;import com.github.soonboylena.myflow.vModel.UiObject;import lombok.Data;import java.util.ArrayList;import java.util.List;/** * @Author: zhao peng * @Date: 2017/10/31 * @Time: 9:30 * @Description: 条件过滤组件 选择部分 */@Datapublic class FilterSelect implements UiObject {    private transient String Id;    private List<SelectNode> child;    public FilterSelect() {        this.child = new ArrayList<>();    }    public void addChild(String lable, String value) {        this.child.add(new SelectNode(lable, value));    }}@Dataclass SelectNode implements IUiDefinition {    String label;    String value;    SelectNode(String label, String value) {        this.label = label;        this.value = value;    }}