package com.github.soonboylena.myflow.dynamic.vModel.uiComponent;import com.github.soonboylena.myflow.dynamic.vModel.AbstractDwc;import com.github.soonboylena.myflow.dynamic.vModel.IUiDefinition;import lombok.Data;import java.util.ArrayList;import java.util.List;/** * Created with IntelliJ IDEA. * Description: * Project Name:    spring-boot-admin * LoginInfoEntity:            sunb * Date:            2017-11-11 * Time:            16:00 */public class DetailTable extends AbstractDwc<DetailTableDefine> {    private transient static final String TYPE = "m-detail-table";    @Override    public String getType() {        return TYPE;    }    public DetailTable() {        if (getDefine() == null) {            setDefine(new DetailTableDefine());        }    }    public void setTableColumns(String name, List<DetailColumn> collect) {        getDefine().setName(name);        getDefine().setColumns(collect);    }    public void addTableColumn(DetailColumn o) {        getDefine().getColumns().add(o);    }    public void setAlias(String alias) {        getDefine().setAlias(alias);    }    public void setButtons(List<Button> buttons) {        if (buttons != null) {            getDefine().setButtons(buttons);        }    }    public void setIdKey(String idKey) {        getDefine().setIdKey(idKey);    }    /**     * add del edit     */    public void setStatus(boolean add, boolean delete, boolean edit) {        List<String> strings = new ArrayList<>(3);        if (add) strings.add("add");        if (delete) strings.add("del");        if (edit) strings.add("edit");        getDefine().setStatus(String.join("_", strings));    }}@Dataclass DetailTableDefine implements IUiDefinition {    private String name;    private List<DetailColumn> columns = new ArrayList<>();    private String alias;    private List<Button> buttons;    private String idKey;    private String status;}