package com.github.soonboylena.myflow.dynamic.vModel.uiComponent;import com.fasterxml.jackson.annotation.JsonProperty;import com.github.soonboylena.myflow.dynamic.vModel.AbstractContainer;import com.github.soonboylena.myflow.dynamic.vModel.AbstractContainerDefinition;import com.github.soonboylena.myflow.dynamic.vModel.UiObject;import com.github.soonboylena.myflow.dynamic.vModel.uiAction.UrlObject;import lombok.AllArgsConstructor;import lombok.NoArgsConstructor;import java.util.ArrayList;import java.util.HashMap;import java.util.List;import java.util.Map;public class Page extends AbstractContainer<PageDefinition> {    private transient static final String TYPE = "nPage";    private Map<String, Integer> formCountMap = new HashMap<>();    public Page() {        setDefine(new PageDefinition());    }    public Page(String caption) {        super();        setDefine(new PageDefinition(caption));    }    public Page(String title, String subtitle) {        super();        setDefine(new PageDefinition(title, subtitle));    }    private PageDefinition getDefinition() {        return null != this.getDefine() ? this.getDefine() : new PageDefinition();    }    public void setSubTitle(String subTitle) {        getDefinition().setSubTitle(subTitle);    }    public void addBtn(Button button) {        getDefinition().addBtn(button);    }    public void addBtn(List<Button> buttons) {        getDefinition().addBtn(buttons);    }    public void setDataUrl(UrlObject dataUrl) {        getDefinition().setDataUrl(dataUrl);    }    public void setStatusUrl(UrlObject statusUrl) {        getDefinition().setStatusUrl(statusUrl);    }    public void setBackUrl(String backUrl) {        getDefinition().setBackUrl(backUrl);    }    @Override    public void addContent(UiObject uiObject) {        if (uiObject instanceof Form) {            getDefinition().setForm((Form) uiObject);        } else super.addContent(uiObject);    }    @Override    public String getType() {        return TYPE;    }    public void setData(Map<String, Map<String, Object>> data) {        getDefinition().setData(data);    }}@NoArgsConstructor@AllArgsConstructorclass PageDefinition extends AbstractContainerDefinition {    private String caption;    private String subTitle;    private List<Button> buttons;    @JsonProperty(value = "data_url")    private UrlObject dataUrl;    @JsonProperty(value = "status_url")    private UrlObject statusUrl;    private String backUrl;    private Form form;    private Map<String, Map<String, Object>> data;    public PageDefinition(String caption) {        this.caption = caption;    }    public PageDefinition(String caption, String subtitle) {        this.caption = caption;        this.subTitle = subtitle;    }    public void addBtn(Button button) {        if (null == this.getButtons()) {            this.buttons = new ArrayList<>();        }        this.buttons.add(button);    }    public void addBtn(List<Button> button) {        if (null == this.getButtons()) {            this.buttons = new ArrayList<>();        }        this.buttons.addAll(button);    }    public String getSubTitle() {        return subTitle;    }    public void setSubTitle(String subTitle) {        this.subTitle = subTitle;    }    public List<Button> getButtons() {        return buttons;    }    public void setButtons(List<Button> buttons) {        this.buttons = buttons;    }    public UrlObject getDataUrl() {        return dataUrl;    }    public void setDataUrl(UrlObject dataUrl) {        this.dataUrl = dataUrl;    }    public UrlObject getStatusUrl() {        return statusUrl;    }    public void setStatusUrl(UrlObject statusUrl) {        this.statusUrl = statusUrl;    }    public String getBackUrl() {        return backUrl;    }    public void setBackUrl(String backUrl) {        this.backUrl = backUrl;    }    public Form getForm() {        return form;    }    public void setForm(Form form) {        this.form = form;    }    public void setData(Map<String, Map<String, Object>> data) {        this.data = data;    }    public Map<String, Map<String, Object>> getData() {        return data;    }}