package com.github.soonboylena.myflow.dynamic.vModel.uiComponent;import com.github.soonboylena.myflow.dynamic.vModel.AbstractContainer;import com.github.soonboylena.myflow.dynamic.vModel.IContainerDefinition;import lombok.Data;/** * Created with IntelliJ IDEA. * Description: * Project Name:    spring-boot-admin * LoginInfoEntity:            sunb * Date:            2017-10-24 * Time:            14:43 */public class Section extends AbstractContainer<SectionUiDefine> {    private transient static final String TYPE = "m-section";    public Section() {        setDefine(new SectionUiDefine());    }    public Section(String caption) {        this();        getDefine().setCaption(caption);    }    @Override    public String getType() {        return TYPE;    }}@Dataclass SectionUiDefine implements IContainerDefinition {    private String caption;    @Override    public void setCaption(String caption) {        this.caption = caption;    }}